#
#     ax = fig.add_subplot(2, 3, i+1, projection='3d')
#     ax.plot_surface(X, Y, Z, cmap=pl.cm.viridis, linewidth=0, rstride=1, cstride=1)
#     ax.set_aspect('equal')
#     ax.set_title(mode)
#
# fig.tight_layout()
#
# pl.show()
# \endcode
